startup:
[ $IW4M == 1 ] && sleep 30 && "${HOME}/iw4m-log-server" -p "${SERVER_PORT}" &
wine64 boiii.exe -dedicated -headless +set fs_game "{{MOD_FOLDER}}" +set net_port "{{SERVER_PORT}}" +set logfile 2 +exec "{{SERVER_CFG}}" &
{ chmod +x cod-rcon; sleep 30; echo "Running RCON client"; ./cod-rcon -O -P "{{SERVER_PORT}}" -p {{RCON_PASSWORD}}; }

config.files:
{
    "zone/server.cfg": {
        "parser": "file",
        "find": {
            "set rcon_password": "set rcon_password \"{{server.build.env.RCON_PASSWORD}}\""
        }
    },
    "zone/server_cp.cfg": {
        "parser": "file",
        "find": {
            "set rcon_password": "set rcon_password \"{{server.build.env.RCON_PASSWORD}}\""
        }
    },
    "zone/server_zm.cfg": {
        "parser": "file",
        "find": {
            "set rcon_password": "set rcon_password \"{{server.build.env.RCON_PASSWORD}}\""
        }
    }
}
